import React, { useState, useRef, useEffect,  } from "react";
import "./Navbar.css";

import { Link } from "react-router-dom";

export const Nav = () => {
    const [menuAbierto, setMenuAbierto] = useState(false);
    const menuRef = useRef(null);
    const manejarAlternarMenu = () => {
        setMenuAbierto(!menuAbierto);
    };
    const handleClickFuera = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuAbierto(false);
        }
    };
    useEffect(() => {
        document.addEventListener('mousedown', handleClickFuera);
        return () => {
            document.removeEventListener('mousedown', handleClickFuera);
        };
    }, []);



    return (
        <div className="container-nav">
            <div className="icono-menu" onClick={manejarAlternarMenu}>
                <span className={"material-symbols-outlined ${menuAbierto ? 'abierto' : ''}"} >
                    menu
                </span>
            </div>

            <div ref={menuRef} className={`contenedor-menu ${menuAbierto ? 'activo' : ''}`}>
                <nav className="menu">
                    <ul>
                    <li ><Link to="/">Inicio</Link></li>

                        <li><button className="btn btn-secondary btn-sm dropdown-toggle " type="button" data-bs-toggle="dropdown" aria-expanded="false"  >
                            Buscar por edad
                        </button>
                            <ul className="dropdown-menu">
                                <li className="dropdown-item" categoria="1" ><Link to="/filtro" categoria= "1">0-3 Meses</Link></li>
                                {/* <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="2" />)}>3-6 meses</li>
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="3" />)}>6-12 meses</li>
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="4" />)}>+ 1 año</li>
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="5" />)}>+ 3 años</li> */}
                            </ul>
                        </li>
                        <li><button className="btn btn-secondary btn-sm dropdown-toggle " type="button" data-bs-toggle="dropdown" aria-expanded="false"  >
                            Buscar por categoria
                        </button>
                            {/* <ul className="dropdown-menu">
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="Montessori" />)}>Juguetes Montessori</li>
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="Didactico" />)}>Juguetes didacticos</li>
                                <li className="dropdown-item" onClick={() => cambiarVista(<FiltroCateg categoria="Otros" />)}>Juegos de mesa y otros</li>
                            </ul> */}
                        </li>
                        <li ><Link to="/todos">Ver todos</Link></li>
                        <li><button type="button" className=" btn btn-sm btn-secondary">Nosotros</button></li>
                    </ul>
                </nav>
                
            </div>
        </div>
    );
};
export default Nav;



